(person_name LIKE CONCAT('%', #{keyword}, '%') OR idcard LIKE CONCAT('%', #{keyword}, '%') OR company_code LIKE CONCAT('%', #{keyword}, '%'))
AND company_code IN
#{c}
ORDER BY id DESC LIMIT #{pageSize} OFFSET #{offset}
